import { Component, OnInit, OnDestroy } from '@angular/core';
import { Product, ProductService } from '../product.service';
import { Subject, Observable, of } from 'rxjs';
import { debounceTime, distinctUntilChanged, switchMap, catchError, startWith, map } from 'rxjs/operators';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit, OnDestroy {
  products$: Observable<Product[]>;
  filteredProducts: Product[] = [];
  allProducts: Product[] = [];
  loading: boolean = true;
  errorMessage: string | null = null;

  private searchTerms = new Subject<string>();
  private destroy$ = new Subject<void>();

  constructor(private productService: ProductService) { }

  ngOnInit(): void {
    this.products$ = this.productService.getProducts().pipe(
      startWith([]), // Emit an empty array initially
      catchError(error => {
        this.errorMessage = error.message;
        this.loading = false;
        return of([]); // Return an empty observable on error
      }),
      map(products => {
        this.allProducts = products;
        this.loading = false;
        this.applyFilter(''); // Apply initial empty filter
        return products; // Return original products for now, filtering handled separately
      })
    );

    // Subscribe to search terms for filtering
    this.searchTerms.pipe(
      debounceTime(300),        // Wait for 300ms pause in events
      distinctUntilChanged(),   // Ignore if next search term is same as previous
      // No need for switchMap to API, as we filter client-side
      // If filtering was server-side, switchMap would call the service
      // switchMap((term: string) => this.productService.searchProducts(term))
      // .takeUntil(this.destroy$) // For proper cleanup
    ).subscribe(term => {
      this.applyFilter(term);
    });

    // Initial load of products
    this.products$.subscribe(products => {
      // The map operator already sets allProducts and calls applyFilter
    });
  }

  // Push a search term into the observable stream
  search(term: string): void {
    this.searchTerms.next(term);
  }

  // Apply client-side filtering
  private applyFilter(term: string): void {
    const lowerCaseTerm = term.toLowerCase();
    this.filteredProducts = this.allProducts.filter(product =>
      product.name.toLowerCase().includes(lowerCaseTerm) ||
      product.description.toLowerCase().includes(lowerCaseTerm)
    );
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }
}
