// --- tasktracker.js ---

// Task Class
class Task {
    constructor(id, title, description, dueDate, priority, isComplete = false) {
        this.id = id;
        this.title = title;
        this.description = description;
        this.dueDate = new Date(dueDate); // Store as Date object
        this.priority = priority; // low, medium, high
        this.isComplete = isComplete;
    }

    // Static method to create a Task from a plain object (e.g., from localStorage)
    static fromObject(obj) {
        return new Task(obj.id, obj.title, obj.description, obj.dueDate, obj.priority, obj.isComplete);
    }
}

// TaskTracker Class
class TaskTracker {
    constructor() {
        this.tasks = this.loadTasks();
        this.nextTaskId = this.getNextTaskId();
    }

    // --- Data Persistence (Feature 11) ---
    loadTasks() {
        try {
            const tasksJSON = localStorage.getItem('tasks');
            if (tasksJSON) {
                const parsedTasks = JSON.parse(tasksJSON);
                // Re-hydrate tasks into Task objects
                return parsedTasks.map(taskObj => Task.fromObject(taskObj));
            }
        } catch (error) {
            console.error("Error loading tasks from local storage:", error);
            // In case of parsing error, start with an empty array
            return [];
        }
        return [];
    }

    saveTasks() {
        try {
            localStorage.setItem('tasks', JSON.stringify(this.tasks));
        } catch (error) {
            console.error("Error saving tasks to local storage:", error);
            // Optionally, inform the user about the saving issue
            alert("Warning: Could not save tasks to local storage. Your changes might not persist.");
        }
    }

    getNextTaskId() {
        if (this.tasks.length === 0) {
            return 1;
        }
        const maxId = Math.max(...this.tasks.map(task => task.id));
        return maxId + 1;
    }

    // --- Data Validation (Feature 7) ---
    validateTaskInput(title, description, dueDate, priority) {
        const errors = [];

        if (!title || title.trim() === '') {
            errors.push("Title cannot be empty.");
        }
        if (title.length > 100) {
            errors.push("Title cannot exceed 100 characters.");
        }

        if (!description || description.trim() === '') {
             errors.push("Description cannot be empty.");
        }
        if (description.length > 500) {
            errors.push("Description cannot exceed 500 characters.");
        }

        const parsedDate = new Date(dueDate);
        if (isNaN(parsedDate.getTime())) {
            errors.push("Invalid due date format. Please use YYYY-MM-DD.");
        } else if (parsedDate < new Date()) {
            // Optional: Prevent setting due dates in the past
            // errors.push("Due date cannot be in the past.");
        }

        const validPriorities = ['low', 'medium', 'high'];
        if (!priority || !validPriorities.includes(priority.toLowerCase())) {
            errors.push("Priority must be 'low', 'medium', or 'high'.");
        }

        return errors;
    }

    // --- Add a Task (Feature 1) ---
    addTask(title, description, dueDate, priority) {
        const validationErrors = this.validateTaskInput(title, description, dueDate, priority);
        if (validationErrors.length > 0) {
            console.error("Task validation failed:", validationErrors);
            return { success: false, errors: validationErrors };
        }

        const newTask = new Task(this.nextTaskId++, title, description, dueDate, priority.toLowerCase());
        this.tasks.push(newTask);
        this.saveTasks();
        return { success: true, task: newTask };
    }

    // --- List Tasks (Feature 2) ---
    listTasks(tasksToList = this.tasks) {
        if (tasksToList.length === 0) {
            return "No tasks to display.";
        }

        let output = "";
        tasksToList.forEach(task => {
            const status = task.isComplete ? "[COMPLETE]" : "[PENDING]";
            output += `ID: ${task.id} ${status}\n`;
            output += `  Title: ${task.title}\n`;
            output += `  Description: ${task.description}\n`;
            output += `  Due Date: ${task.dueDate.toLocaleDateString()}\n`;
            output += `  Priority: ${task.priority}\n`;
            output += `--------------------\n`;
        });
        return output;
    }

    // --- Mark Task as Complete (Feature 3) ---
    markTaskComplete(taskId) {
        const task = this.tasks.find(t => t.id === taskId);
        if (task) {
            task.isComplete = true;
            this.saveTasks();
            return { success: true, message: `Task '${task.title}' (ID: ${taskId}) marked as complete.` };
        }
        return { success: false, message: `Task with ID ${taskId} not found.` };
    }

    // --- Delete Task (Feature 4) ---
    deleteTask(taskId) {
        const initialLength = this.tasks.length;
        this.tasks = this.tasks.filter(t => t.id !== taskId);
        if (this.tasks.length < initialLength) {
            this.saveTasks();
            return { success: true, message: `Task with ID ${taskId} deleted.` };
        }
        return { success: false, message: `Task with ID ${taskId} not found.` };
    }

    // --- Search and Filter (Feature 5) ---
    searchTasks(keyword) {
        const lowerKeyword = keyword.toLowerCase();
        return this.tasks.filter(task =>
            task.title.toLowerCase().includes(lowerKeyword) ||
            task.description.toLowerCase().includes(lowerKeyword)
        );
    }

    filterTasksByDueDate(startDate, endDate) {
        const start = startDate ? new Date(startDate) : null;
        const end = endDate ? new Date(endDate) : null;

        if ((start && isNaN(start.getTime())) || (end && isNaN(end.getTime()))) {
            return { success: false, message: "Invalid date format for filter. Please use YYYY-MM-DD." };
        }

        return {
            success: true,
            tasks: this.tasks.filter(task => {
                const taskDueDate = task.dueDate;
                const matchesStart = start ? taskDueDate >= start : true;
                const matchesEnd = end ? taskDueDate <= end : true;
                return matchesStart && matchesEnd;
            })
        };
    }

    // --- Sorting (Feature 6) ---
    sortTasksByPriority() {
        const priorityOrder = { 'high': 3, 'medium': 2, 'low': 1 };
        return [...this.tasks].sort((a, b) => {
            // Sort incomplete tasks before complete tasks
            if (a.isComplete !== b.isComplete) {
                return a.isComplete ? 1 : -1;
            }
            // Then sort by priority
            return priorityOrder[b.priority] - priorityOrder[a.priority];
        });
    }

    // --- Statistics (Feature 8) ---
    getStatistics() {
        const totalTasks = this.tasks.length;
        const completedTasks = this.tasks.filter(task => task.isComplete).length;
        const pendingTasks = totalTasks - completedTasks;

        const now = new Date();
        const nextWeek = new Date();
        nextWeek.setDate(now.getDate() + 7);

        const tasksDueNextWeek = this.tasks.filter(task =>
            !task.isComplete &&
            task.dueDate >= now &&
            task.dueDate <= nextWeek
        ).length;

        return {
            totalTasks,
            completedTasks,
            pendingTasks,
            tasksDueNextWeek
        };
    }
}

// --- User Interface (Feature 9) - Command Line Interface Example ---
// This part demonstrates a simple CLI. For a web browser UI, you'd use HTML, CSS, and DOM manipulation.

const tracker = new TaskTracker();

function displayMenu() {
    console.log("\n--- Task Tracker Menu ---");
    console.log("1. Add Task");
    console.log("2. List All Tasks");
    console.log("3. Mark Task Complete");
    console.log("4. Delete Task");
    console.log("5. Search Tasks");
    console.log("6. Filter Tasks by Due Date");
    console.log("7. Sort Tasks by Priority");
    console.log("8. Show Statistics");
    console.log("9. Exit");
    console.log("-------------------------");
}

// Helper for getting user input (Node.js specific for CLI)
const readline = require('readline').createInterface({
    input: process.stdin,
    output: process.stdout
});

function prompt(question) {
    return new Promise(resolve => readline.question(question, resolve));
}

async function handleUserInput() {
    let running = true;
    while (running) {
        displayMenu();
        const choice = await prompt("Enter your choice: ");

        // --- Error Handling (Feature 10) ---
        try {
            switch (choice.trim()) {
                case '1':
                    console.log("\n--- Add New Task ---");
                    const title = await prompt("Enter title: ");
                    const description = await prompt("Enter description: ");
                    const dueDate = await prompt("Enter due date (YYYY-MM-DD): ");
                    const priority = await prompt("Enter priority (low, medium, high): ");
                    const addResult = tracker.addTask(title, description, dueDate, priority);
                    if (addResult.success) {
                        console.log(`Task '${addResult.task.title}' added successfully!`);
                    } else {
                        console.error("Failed to add task:", addResult.errors.join(", "));
                    }
                    break;
                case '2':
                    console.log("\n--- All Tasks ---");
                    console.log(tracker.listTasks());
                    break;
                case '3':
                    const completeId = parseInt(await prompt("Enter Task ID to mark complete: "));
                    const completeResult = tracker.markTaskComplete(completeId);
                    if (completeResult.success) {
                        console.log(completeResult.message);
                    } else {
                        console.error(completeResult.message);
                    }
                    break;
                case '4':
                    const deleteId = parseInt(await prompt("Enter Task ID to delete: "));
                    const deleteResult = tracker.deleteTask(deleteId);
                    if (deleteResult.success) {
                        console.log(deleteResult.message);
                    } else {
                        console.error(deleteResult.message);
                    }
                    break;
                case '5':
                    const searchKeyword = await prompt("Enter keyword to search: ");
                    const searchResults = tracker.searchTasks(searchKeyword);
                    console.log("\n--- Search Results ---");
                    console.log(tracker.listTasks(searchResults));
                    break;
                case '6':
                    const filterStartDate = await prompt("Enter start date for filter (YYYY-MM-DD, leave empty for no start): ");
                    const filterEndDate = await prompt("Enter end date for filter (YYYY-MM-DD, leave empty for no end): ");
                    const filterResult = tracker.filterTasksByDueDate(filterStartDate || null, filterEndDate || null);
                    if (filterResult.success) {
                        console.log("\n--- Filtered Tasks ---");
                        console.log(tracker.listTasks(filterResult.tasks));
                    } else {
                        console.error(filterResult.message);
                    }
                    break;
                case '7':
                    console.log("\n--- Tasks Sorted by Priority ---");
                    const sortedTasks = tracker.sortTasksByPriority();
                    console.log(tracker.listTasks(sortedTasks));
                    break;
                case '8':
                    console.log("\n--- Task Statistics ---");
                    const stats = tracker.getStatistics();
                    console.log(`Total Tasks: ${stats.totalTasks}`);
                    console.log(`Completed Tasks: ${stats.completedTasks}`);
                    console.log(`Pending Tasks: ${stats.pendingTasks}`);
                    console.log(`Tasks due in next 7 days: ${stats.tasksDueNextWeek}`);
                    break;
                case '9':
                    running = false;
                    console.log("Exiting Task Tracker. Goodbye!");
                    break;
                default:
                    console.log("Invalid choice. Please try again.");
            }
        } catch (error) {
            console.error("An unexpected error occurred:", error.message);
            // Log full error for debugging
            // console.error(error);
        }
    }
    readline.close();
}

// Start the CLI
handleUserInput();

